query tx ($hash: String = "") {
  transaction(where: {hash:{_eq:$hash}}) {
    height
    hash
    success
    tx_signers {
      address: fee_payer
    }
    memo
    gas_wanted
    gas_used
    gas_limit: fee(path: "gas_limit")
    gas_fee {
      fee_amount
      fee_denom
      receivers
    }
    time: block {
      timestamp
    }
    msgs {
      index
      type
      body: value
      parsed_message {
        description
        detail
      }
    }
    raw_log
  }
}

